INSERT INTO productos (id, codigo, nombre, precio, descripcion, imagen) VALUES
(1, 'PROD-001', 'Proteína Whey Isolada 1kg', 35.99, 'Proteína de suero aislada de alta pureza. Ideal para recuperación muscular y ganancia de masa. Sabor a vainilla francesa, mezcla rápida y fácil de digerir.', 'https://via.placeholder.com/200?text=Proteina+Whey'),
(2, 'PROD-002', 'Creatina Monohidrato 500g', 19.99, 'Creatina micronizada de máxima pureza. Aumenta la fuerza, resistencia y masa muscular. Apto para todo tipo de deportistas. Sin sabor, mezcla perfecta.', 'https://via.placeholder.com/200?text=Creatina'),
(3, 'PROD-003', 'Toallas de Microfibra Pack x2', 12.50, 'Pack de 2 toallas de microfibra absorbentes. Perfectas para el gimnasio, secado rápido y compactas para transportar. Colores gris y negro.', 'https://via.placeholder.com/200?text=Toallas'),
(4, 'PROD-004', 'BCAA 3:1:2 500g', 24.99, 'Aminoácidos ramificados en proporción óptima. Previene el catabolismo muscular, mejora el rendimiento y acelera la recuperación. Sabor a limón.', 'https://via.placeholder.com/200?text=BCAA'),
(5, 'PROD-005', 'Botella de Agua Térmica 1L', 29.99, 'Botella aislante de acero inoxidable. Mantiene bebidas frías 24h o calientes 12h. Libre de BPA, resistente y con asa de transporte. Color plateado.', 'https://via.placeholder.com/200?text=Botella'),
(6, 'PROD-006', 'Straps', 18.50, 'Straps profesionales con grip reforzado y soporte de muñeca. Material transpirable y duradero.', 'https://via.placeholder.com/200?text=Guantes'),
(7, 'PROD-007', 'Cinturón de Levantamiento Neopreno', 22.99, 'Cinturón lumbar de neopreno para estabilización. Ideal para sentadillas y levantamientos pesados. Ajuste con velcro, soporte premium y tallas variadas.', 'https://via.placeholder.com/200?text=Cinturon');
